const isSame=str=>str.split("").reverse().join("")===str,fn1=originStr=>{const results=[],back=(start,result)=>{if(start!==originStr.length)for(let i=start;i<originStr.length;i++){let tempStr=originStr.slice(start,i+1);isSame(tempStr)&&back(i+1,result.push(tempStr))}else results.push(result)};return back(0,[]),results};console.log(fn1("a")),console.log(fn1("aba")),console.log(fn1("abcb"));